import './UserInput.css'
import { useState } from "react";

const UserInput = (props) => {

    const [name, setName] = useState('');
    const [age, setAge] = useState('');

    const onChangeName = (event) => {setName(event.target.value);};
    const onChangeAge = (event) => {setAge(event.target.value);};

    const onSubmitHandler = (event) => {
        event.preventDefault();
        const newUser = {
            name: name,
            age: age
        }
        props.onSubmitForm(newUser);
    }

    return(
        <div>
            <form className="form-control" onSubmit={onSubmitHandler}>
                <label>Name : </label><br />
                <input type="text" value={name} onChange={onChangeName}/><br />
                <label>Age : </label><br />
                <input type="number" value={age} onChange={onChangeAge}/>
                <button type="submit">SUBMIT</button>
            </form>
        </div>
    );
};

export default UserInput;
